mapper.update:
UPDATE goods_info
seller_id=#{sellerId}, title=#{title}, price=#{price}, original_price=#{originalPrice}, description=#{description}, category=#{category}, `condition`=#{condition}, is_sold=#{sold}, gmt_modified=now()
WHERE id=#{id}

mapper.select:
SELECT gi.id AS gi_id,seller_id,title,price,original_price,description,`condition`,is_sold,1 AS `type`,
gi.gmt_create AS gi_gmt_create,gi.gmt_modified AS gi_gmt_modified,
`c`.id AS c_id,category_id,category_designation,
`c`.gmt_create AS c_gmt_create,`c`.gmt_modified AS c_gmt_modified,
i.id AS i_id,i.goods_id,image_link,
i.gmt_create AS i_gmt_create,i.gmt_modified AS i_gmt_modified,
t.id AS t_id,content,
t.gmt_create AS t_gmt_create,t.gmt_modified AS t_gmt_modified,
`uu`.username AS uusername
FROM goods_info AS gi
LEFT JOIN goods_category AS c ON gi.category=c.category_id
LEFT JOIN goods_image AS i ON i.goods_id=gi.id
LEFT JOIN goods_tag AS `gt` ON `gt`.goods_id=gi.id
LEFT JOIN tag_of_goods AS t ON t.id=`gt`.tag_id
LEFT JOIN `user` AS `uu` ON `uu`.id= gi.seller_id

    AND seller_id=#{info.sellerId}
    AND price=#{price}
    
        
            AND title LIKE "%"#{info.title}"%"
        
        
            AND title=#{info.title}
        
    
    
        
            AND content LIKE "%"#{tag.content}"%"
        
        
            AND content=#{tag.content}
        
    
    AND original_price=#{info.originalPrice}
    
        
            AND description LIKE "%"#{info.description}"%"
        
        
            AND description=#{info.description}
        
    
    AND category_id=#{info.category}
    AND is_sold=#{info.sold}
    AND `condition`=#{info.condition}
    
        AND gi.gmt_create BETWEEN #{info.createTimeBegin} AND #{info.createTimeEnd}
    

limit #{info.begin},#{info.size}

SELECT gi.id AS gi_id,seller_id,title,price,original_price,description,`condition`,is_sold,1 AS `type`, gi.gmt_create AS gi_gmt_create,gi.gmt_modified AS gi_gmt_modified, `c`.id AS c_id,category_id,category_designation, `c`.gmt_create AS c_gmt_create,`c`.gmt_modified AS c_gmt_modified, i.id AS i_id,i.goods_id,image_link, i.gmt_create AS i_gmt_create,i.gmt_modified AS i_gmt_modified, t.id AS t_id,content, t.gmt_create AS t_gmt_create,t.gmt_modified AS t_gmt_modified, `uu`.username AS uusername FROM goods_info AS gi LEFT JOIN goods_category AS c ON gi.category=c.category_id LEFT JOIN goods_image AS i ON i.goods_id=gi.id LEFT JOIN goods_tag AS `gt` ON `gt`.goods_id=gi.id LEFT JOIN tag_of_goods AS t ON t.id=`gt`.tag_id LEFT JOIN `user` AS `uu` ON `uu`.id= gi.seller_id WHERE (title LIKE "%"#{info.title}"%" OR content LIKE "%"#{tag.content}"%" OR description LIKE "%"#{info.description}"%") AND is_sold=#{info.sold}
AND category_id=#{info.category}
AND `condition`=#{info.condition}
AND gi.gmt_create BETWEEN #{info.createTimeBegin} AND #{info.createTimeEnd}
ORDER BY gi_gmt_create DESC limit #{info.begin},#{info.size}

SELECT gi.id AS gi_id,seller_id,title,price,original_price,description,`condition`,is_sold,1 AS `type`, gi.gmt_create AS gi_gmt_create,gi.gmt_modified AS gi_gmt_modified, `c`.id AS c_id,category_id,category_designation, `c`.gmt_create AS c_gmt_create,`c`.gmt_modified AS c_gmt_modified, i.id AS i_id,i.goods_id,image_link, i.gmt_create AS i_gmt_create,i.gmt_modified AS i_gmt_modified, t.id AS t_id,content, t.gmt_create AS t_gmt_create,t.gmt_modified AS t_gmt_modified FROM goods_info AS gi LEFT JOIN goods_category AS c ON gi.category=c.category_id LEFT JOIN goods_image AS i ON i.goods_id=gi.id LEFT JOIN goods_tag AS `gt` ON `gt`.goods_id=gi.id LEFT JOIN tag_of_goods AS t ON t.id=`gt`.tag_id
WHERE (title LIKE "%"#{info.title}"%" OR content LIKE "%"#{tag.content}"%" OR description LIKE "%"#{info.description}"%") AND is_sold=#{info.sold}
AND category_id=#{info.category}
AND `condition`=#{info.condition}
AND gi.gmt_create BETWEEN #{info.createTimeBegin} AND #{info.createTimeEnd}
ORDER BY `condition` ASC limit #{info.begin},#{info.size}

