mapper.update:
UPDATE activity_info
pub_id=#{pubId}, title=#{title}, address=#{address}, start_time=#{startTime}, end_time=#{endTime}, description=#{description}, category=#{category}, gmt_modified=now()
WHERE id=#{id}

mapper.select:
SELECT a.id AS a_id, pub_id,title,address,start_time,end_time,description,3 AS `type`, a.gmt_create AS a_gmt_create,a.gmt_modified AS a_gmt_modified, `c`.id AS c_id,category_id,category_designation, `c`.gmt_create AS c_gmt_create,`c`.gmt_modified AS c_gmt_modified, i.id AS i_id,i.activity_id,image_link, i.gmt_create AS i_gmt_create,i.gmt_modified AS i_gmt_modified, t.id AS t_id,content, t.gmt_create AS t_gmt_create,t.gmt_modified AS t_gmt_modified, `uu`.username AS uusername FROM activity_info AS a LEFT JOIN activity_category AS c ON a.category=c.category_id LEFT JOIN activity_image AS i ON i.activity_id=a.id LEFT JOIN activity_tag AS `at` ON `at`.activity_id=a.id LEFT JOIN tag_of_activity AS t ON t.id=`at`.tag_id LEFT JOIN `user` AS `uu` ON `uu`.id= a.pub_id
a.end_time >= now() AND pub_id=#{info.pubId}
AND title LIKE "%"#{info.title}"%"
AND title=#{info.title}
AND content LIKE "%"#{tag.content}"%"
AND content=#{tag.content}
AND address LIKE "%"#{info.address}"%"
AND address=#{info.address}
AND description LIKE "%"#{info.description}"%"
AND description=#{info.description}
AND category_id=#{info.category}
AND a.gmt_create BETWEEN #{info.createTimeBegin} AND #{info.createTimeEnd}
ORDER BY a_gmt_create DESC limit #{info.begin},#{info.size}

SELECT a.id AS a_id, pub_id,title,address,start_time,end_time,description,3 AS `type`, a.gmt_create AS a_gmt_create,a.gmt_modified AS a_gmt_modified, `c`.id AS c_id,category_id,category_designation, `c`.gmt_create AS c_gmt_create,`c`.gmt_modified AS c_gmt_modified, i.id AS i_id,i.activity_id,image_link, i.gmt_create AS i_gmt_create,i.gmt_modified AS i_gmt_modified, t.id AS t_id,content, t.gmt_create AS t_gmt_create,t.gmt_modified AS t_gmt_modified, `uu`.username AS uusername FROM activity_info AS a LEFT JOIN activity_category AS c ON a.category=c.category_id LEFT JOIN activity_image AS i ON i.activity_id=a.id LEFT JOIN activity_tag AS `at` ON `at`.activity_id=a.id LEFT JOIN tag_of_activity AS t ON t.id=`at`.tag_id LEFT JOIN `user` AS `uu` ON `uu`.id= a.pub_id WHERE (title LIKE "%"#{info.title}"%"
OR content LIKE "%"#{tag.content}"%"
OR address LIKE "%"#{info.address}"%"
OR description LIKE "%"#{info.description}"%")
AND category_id=#{info.category}
AND a.gmt_create BETWEEN #{info.createTimeBegin} AND #{info.createTimeEnd}
AND a.end_time >= now()
ORDER BY a_gmt_create DESC limit #{info.begin},#{info.size}

